import React from 'react'
import * as BooksAPI from './BooksAPI'
import './App.css'
import { Route, Link } from 'react-router-dom'
import BookShelf from './BookShelf'
import BooksSearch from './BooksSearch'

class BooksApp extends React.Component {
  state = {
    Books: []
  }

  componentDidMount() {
    BooksAPI.getAll().then((Books) => {
      this.setState({
        Books: books
      })
    })
  }

moveBookShelf = (book, newValue) => {
  book.props.book.shelf = newValue;
  this.setState((state) => ({
    books: state.books.filter((b) => b.id !== book.props.book.id).concat([book.props.book])
  }))
    BooksAPI.update(book.props.book, newValue);
}

  render() {
    return (
      <div className="app">
        <Route exact path="/" render={() => (<BookShelf books={this.state.Books} onChange={this.update_books_details}/>)}/>
        <Route exact path="/search" render={({history}) => (<BooksSearch onChange={this.update_books_details} myBooks={this.state.Books}/>)}/>
      </div>
    )
  }
}

export default BooksApp
